import type { UserRole } from "@prisma/client"

declare module "next-auth" {
  interface Session {
    user: {
      id: string
      email: string
      name: string
      role: UserRole
      storeId?: string
    }
  }

  interface User {
    id: string
    email: string
    name: string
    role: UserRole
    storeId?: string
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    id: string
    role: UserRole
    storeId?: string
  }
}

